            "tripItemDescription": "string",
            "tripItemId": 10,
            "tripItemTime": "21:12:00"
          }
        ],
        "tripCode": "Spa",
        "tripCountry": "Singapore",
        "tripDuration": 1,
        "tripItemsList": [
          {
            "tripItemDays": 1,
            "tripItemDescription": "string",
            "tripItemId": 10,
            "tripItemTime": "21:12:00"
          }
        ],
        "tripName": "string"
      });
  
      axios.post(
        `/api/v1/premadetrip`, 
        iJSON,config
      ).then(data => console.log(data.data)).catch((error) => {
        console.log(error);
    })
  
      }

  return (
    <div>
      <SimpleImageSlider
        width={896}
        height={504}
        images={images}
        showBullets={true}
        showNavs={true}
      />
      <p><button className = "button" onClick={sendcart}>Add to Cart ðŸ›’</button></p>
    </div>
  );
}

export default SlidingImageRelaxInLuxury;
